import Header from "../components/Header";

class Signup extends React.Component {
  constructor(props) {
    super(props);
    // this.state = { usernamevalue: "", pwvalue: "", emailvalue: "" };

    /*this.usernameChange = this.usernameChange.bind(this);
    this.pwChange = this.pwChange.bind(this);
    this.emailChange = this.emailChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);*/

    const formik = useFormik({
      initialValues: {
        username: "",
        password: "",
        email: ""
      },
      onSubmit: values => {
        alert(JSON.stringify(values, null, 2));
      }
    });
  }

  /* usernameChange(event) {
    this.setState({ usernamevalue: event.target.value });
  }

  pwChange(event) {
    this.setState({ pwvalue: event.target.value });
  }

  emailChange(event) {
    this.setState({ emailvalue: event.target.value });
  }

  handleSubmit(event) {
    alert("successfully submitted" + this.state.value);
    event.preventDefault();
  }*/

  render() {
    return (
      <div>
        <Header />
        <div>
          <div>Sign up</div>
          <form onSubmit={formik.handleSubmit}>
            <label>
              First, choose a unique username
              <input
                type="text"
                name="username"
                value={formik.values.username}
                onChange={formik.handleChange}
              ></input>
            </label>
            <label>
              Enter a password (must contain min. 6 characters)
              <input
                type="text"
                name="password"
                value={formik.values.password}
                onChange={formik.handleChange}
              ></input>
            </label>
            <label>
              Enter your email
              <input
                type="text"
                name="email"
                value={formik.values.email}
                onChange={formik.handleChange}
              ></input>
            </label>
            <input type="submit"></input>
          </form>
        </div>
      </div>
    );
  }
}

export default Signup;
