import Header from "../components/Header";

class Signup extends React.Component {
  constructor(props) {
    super(props);
    this.state = { usernamevalue: "", pwvalue: "", emailvalue: "" };

    this.usernameChange = this.usernameChange.bind(this);
    this.pwChange = this.pwChange.bind(this);
    this.emailChange = this.emailChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  usernameChange(event) {
    this.setState({ usernamevalue: event.target.value });
  }

  pwChange(event) {
    this.setState({ pwvalue: event.target.value });
  }

  emailChange(event) {
    this.setState({ emailvalue: event.target.value });
  }

  handleSubmit(event) {
    alert("successfully submitted" + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <div>
        <Header />
        <div>
          <div>Sign up</div>
          <form>
            <label>
              First, choose a unique username
              <input
                type="text"
                name="username"
                value={this.state.usernamevalue}
                onChange={this.usernameChange}
              ></input>
            </label>
            <label>
              Enter a password (must contain min. 6 characters)
              <input
                type="text"
                name="password"
                value={this.state.pwvalue}
                onChange={this.pwChange}
              ></input>
            </label>
            <label>
              Enter your email
              <input
                type="text"
                name="email"
                value={this.state.emailvalue}
                onChange={this.emailChange}
              ></input>
            </label>
            <input type="submit" value="Submit"></input>
          </form>
        </div>
      </div>
    );
  }
}

export default Signup;
